package ccdec20;

import java.io.BufferedReader;
import java.io.InputStreamReader;
import java.io.PrintWriter;
import java.util.StringTokenizer;

public class AlphaBit2 {
	public static void main(String[] args)throws Exception 
	{	
		//importing io functions
				BufferedReader br= new BufferedReader(new InputStreamReader(System.in));
				PrintWriter out = new PrintWriter(System.out);
				StringTokenizer st= new StringTokenizer(br.readLine());
				int pow2[]= {1, 2, 4, 8, 16, 32, 64, 128, 256, 512, 1024, 2048, 4096, 8192, 16384, 32768, 65536, 131072, 262144, 524288, 1048576, 2097152, 4194304, 8388608, 16777216, 33554432};
				//this is number of test cases
				int tc=Integer.parseInt(st.nextToken());
				String aa="";
				//loop for doing coding 
				while(tc-->0)
				{	String s="";
					st= new StringTokenizer(br.readLine());
					int n=Integer.parseInt(st.nextToken());
					int q=Integer.parseInt(st.nextToken());
					int sptr=0;
					if(q<n)
					{
						System.out.println(-1);
						continue;
					}
					int k=q;
					int jptr=pow2.length-1;
					for (int i = 0; i < n && k>= (n-i); i++) 
					{
						for (int j = jptr; j >=0; j--) 
						{jptr=j;
							if (k-pow2[j]>=(n-1-i))
							{
								String sng=Character.toString((char)(97+j));
								s=s+sng;
								k-=pow2[j];
								break;
								
							}
							
						}
					}
					if (k>0)
					{
						out.println(-1);
					}
					else
					{
								
						int lenDiff=n-s.length();
						s=s+aa.substring(0, lenDiff);
						
						out.println(s);
					}
					//out.flush();
				}
	}
	
}
